import 'package:cloud_firestore/cloud_firestore.dart';import 'package:payme_clone/data/models/sign_up/sign_up_model.dart';import 'package:payme_clone/data/repository/app_repository.dart';import 'package:payme_clone/data/source/remote/api_service.dart';import 'package:payme_clone/di/di.dart';import '../../models/auth_model.dart';import '../../models/sign_up/response/sign_up_response.dart';class AppRepositoryImpl extends AppRepository {  final api = getIt<ApiService>();  @override  Future<bool> checkNumber(AuthModel authModel) async {    try {      QuerySnapshot querySnapshot = await FirebaseFirestore.instance          .collection('users')          .where('number', isEqualTo: authModel.number)          .get();      print(querySnapshot.docs.isNotEmpty);      return querySnapshot.docs.isNotEmpty;    } catch (e) {      print("Error checking phone number: $e");      return false;    }  }  @override  Future<SignUpResponse> signUpUser(SignUpModel signUpModel) async {    final response = await api.signUpUser(signUpModel);    return response;  }}