import 'package:cloud_firestore/cloud_firestore.dart';import 'package:payme_clone/data/repository/app_repository.dart';import '../../models/auth_model.dart';class AppRepositoryImpl extends AppRepository {  @override  Future<bool> checkNumber(AuthModel authModel) async {    try {      QuerySnapshot querySnapshot = await FirebaseFirestore.instance          .collection('users')          .where('number', isEqualTo: authModel.number)          .get();      print(querySnapshot.docs.isNotEmpty);      return querySnapshot.docs.isNotEmpty;    } catch (e) {      print("Error checking phone number: $e");      return false;    }  }}