import 'package:dio/dio.dart';import 'package:get_it/get_it.dart';import 'package:payme_clone/data/repository/app_repository.dart';import 'package:payme_clone/data/repository/impl/app_repository_impl.dart';import 'package:payme_clone/data/source/remote/api_service.dart';final getIt = GetIt.instance;final dio = Dio(BaseOptions(    contentType: 'application/json', receiveDataWhenStatusError: true));void setup() {  getIt.registerSingleton<AppRepository>(AppRepositoryImpl());  getIt.registerSingleton<ApiService>(ApiService(dio));}