import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:google_fonts/google_fonts.dart';import 'package:payme_clone/data/models/add_card/add_card_model.dart';import '../../../utils/utils.dart';import '../../widgets/btn_done_widget.dart';import '../../widgets/input_add_card_widget.dart';import '../../widgets/input_cardname_widget.dart';import '../../widgets/input_expire_widget.dart';import 'bloc/add_card_bloc.dart';class AddCardPage extends StatelessWidget {  AddCardPage({super.key});  final controllerCard = TextEditingController();  final _focusNode = FocusNode();  final controllerExp = TextEditingController();  final controllerExpMonth = TextEditingController();  final controllerCardName = TextEditingController();  @override  Widget build(BuildContext context) {    return BlocProvider(      create: (context) => AddCardBloc(),      child: Scaffold(        backgroundColor: const Color(primaryColor),        appBar: AppBar(          backgroundColor: const Color(primaryColor),          leading: InkWell(              borderRadius: BorderRadius.circular(100),              onTap: () {                Navigator.pop(context);              },              child: const Icon(                Icons.arrow_back_rounded,                color: Colors.white38,              )),          title: Text('Karta qo\'shish',              style: GoogleFonts.poppins(                fontSize: 15,                color: Colors.white,                fontWeight: FontWeight.w500,              )),          actions: const [            Padding(              padding: EdgeInsets.only(right: 16),              child: Icon(                Icons.message_outlined,                color: Colors.white38,              ),            )          ],        ),        body: Padding(          padding: const EdgeInsets.only(top: 16.0, right: 16, left: 16),          child: Column(            children: [              inputAddCardWidget(controllerCard, _focusNode),              const SizedBox(height: 20),              InputExpireWidget(                  hint: 'Amal qilish muddati (yil)', controller: controllerExp),              const SizedBox(height: 10),              InputCardNameWidget(                hint: 'Amal qilish muddati (oy)',                controller: controllerCardName,              ),              InputCardNameWidget(                hint: 'Kartaning nomi',                controller: controllerCardName,              ),              const SizedBox(height: 10),              buttonDone(true, () {                context.read<AddCardBloc>().add(AddNewCardEvent(AddCardModel(                    name: controllerCardName.text,                    expiredMonth: controllerExpMonth.text,                    expiredYear: controllerExp.text,                    pan: controllerCard.text)));              })            ],          ),        ),      ),    );  }}