import 'package:bloc/bloc.dart';import 'package:meta/meta.dart';import 'package:payme_clone/data/preference/my_preference.dart';part 'theme_event.dart';part 'theme_state.dart';class ThemeBloc extends Bloc<ThemeEvent, ThemeState> {  ThemeBloc() : super(ThemeInitial()) {    on<ChangeBgEvent>((event, emit) {      MyPreference.setMainImage(event.str);      emit(ChangeBgState(event.str));    });  }}