import 'package:flutter/material.dart';import 'package:google_fonts/google_fonts.dart';import 'package:payme_clone/utils/utils.dart';Widget buttonDone(bool isClickable, void Function() onClick) {  return Container(    height: 45,    decoration: BoxDecoration(        color: isClickable            ? const Color(pantonColor)            : const Color(pantonColor).withAlpha(20),        borderRadius: const BorderRadius.all(Radius.circular(13))),    child: Center(      child: Text('Done',          style: GoogleFonts.poppins(              color: isClickable                  ? Colors.white                  : const Color(pantonColor).withAlpha(20),              fontSize: 14,              fontWeight: FontWeight.w500)),    ),  );}