import 'package:flutter/material.dart';import 'package:google_fonts/google_fonts.dart';import '../../utils/utils.dart';class InputBornWidget extends StatefulWidget {  final TextEditingController controller;  final String hint;  final bool? realOnly;  Function() onClick;  InputBornWidget(      this.onClick,    {    super.key,    required this.hint,    required this.controller,    this.realOnly  });  @override  _InputBornWidgetState createState() => _InputBornWidgetState();}class _InputBornWidgetState extends State<InputBornWidget> {  bool obscureText = true;  bool isFocused = false;  @override  Widget build(BuildContext context) {    return SingleChildScrollView(      child: Column(        crossAxisAlignment: CrossAxisAlignment.start,        children: [          Padding(            padding: const EdgeInsets.only(left: 15, bottom: 5),            child: Text(              widget.hint,              style: GoogleFonts.poppins(                fontSize: 14,                color: isFocused                    ? Color(                        widget.controller.text.isNotEmpty                            ? widget.controller.text.length < 6                                ? lightRedColor                                : pantonColor                            : pantonColor,                      )                    : Colors.white,                fontWeight: FontWeight.w400,              ),            ),          ),          TextFormField(            onTap: widget.onClick,            // onChanged: widget.onChanged,            autofocus: true,            readOnly: widget.realOnly == null ? false : true,            controller: widget.controller,            // obscureText: obscureText,            decoration: InputDecoration(              hintText: widget.hint,              hintStyle:                  TextStyle(color: const Color(0xFFFFFFFF).withAlpha(80)),              counterText: "",              focusedBorder: OutlineInputBorder(                borderSide: BorderSide(                  color: Color(                    widget.controller.text.isNotEmpty                        ? widget.controller.text.length < 6                            ? lightRedColor                            : pantonColor                        : pantonColor,                  ),                  width: 2,                ),                borderRadius: BorderRadius.circular(13.0),              ),              enabledBorder: OutlineInputBorder(                borderSide: BorderSide(                  color: Color(                    widget.controller.text.isNotEmpty                        ? widget.controller.text.length < 6                            ? lightRedColor                            : pantonColor                        : pantonColor,                  ),                  width: 2,                ),                borderRadius: BorderRadius.circular(13.0),              ),              isDense: true,              filled: true,              fillColor: const Color(0xFF141517),            ),            cursorColor: const Color(pantonColor),            style: GoogleFonts.poppins(fontSize: 14, color: Colors.white),            onChanged: (value) {              setState(() {});            },          ),          const SizedBox(            height: primaryPadding,          )        ],      ),    );  }}