import 'package:flutter/material.dart';import 'package:google_fonts/google_fonts.dart';import 'package:mask_text_input_formatter/mask_text_input_formatter.dart';import '../../utils/utils.dart';class InputCardNameWidget extends StatefulWidget {  final TextEditingController controller;  final String hint;  const InputCardNameWidget({    super.key,    required this.hint,    required this.controller,  });  @override  _InputCardNameWidgetState createState() => _InputCardNameWidgetState();}class _InputCardNameWidgetState extends State<InputCardNameWidget> {  bool obscureText = true;  bool isFocused = false;  @override  Widget build(BuildContext context) {    return SingleChildScrollView(      child: Column(        crossAxisAlignment: CrossAxisAlignment.start,        children: [          TextFormField(            // onChanged: widget.onChanged,            autofocus: true,            controller: widget.controller,            // obscureText: obscureText,            decoration: InputDecoration(              hintText: widget.hint,              hintStyle:              TextStyle(color: const Color(0xFFFFFFFF).withAlpha(80)),              counterText: "",              focusedBorder: OutlineInputBorder(                borderSide: const BorderSide(                  color: Color(pantonColor),                  width: 2,                ),                borderRadius: BorderRadius.circular(13.0),              ),              enabledBorder: OutlineInputBorder(                borderSide: BorderSide(                  color: Color(                    widget.controller.text.isNotEmpty                        ? widget.controller.text.length < 6                        ? lightRedColor                        : pantonColor                        : pantonColor,                  ),                  width: 2,                ),                borderRadius: BorderRadius.circular(13.0),              ),              isDense: true,              filled: true,              fillColor: const Color(0xFF141517),            ),            cursorColor: const Color(pantonColor),            style: GoogleFonts.poppins(fontSize: 14, color: Colors.white),            onChanged: (value) {              setState(() {});            },          ),          const SizedBox(            height: primaryPadding,          )        ],      ),    );  }}