import 'package:flutter/material.dart';import 'package:google_fonts/google_fonts.dart';import '../../utils/utils.dart';class PasswordInputWidget extends StatefulWidget {  final TextEditingController controller;  final FocusNode inputNode;  final void Function(String) onChanged;  const PasswordInputWidget({    super.key,    required this.controller,    required this.inputNode,    required this.onChanged,  });  @override  _PasswordInputWidgetState createState() => _PasswordInputWidgetState();}class _PasswordInputWidgetState extends State<PasswordInputWidget> {  bool obscureText = true;  @override  Widget build(BuildContext context) {    return SingleChildScrollView(      child: Column(        crossAxisAlignment: CrossAxisAlignment.start,        children: [          Padding(            padding: const EdgeInsets.only(left: 15, bottom: 5),            child: Text(              'Password',              style: GoogleFonts.poppins(                fontSize: 14,                color: Color(widget.controller.text.isNotEmpty                    ? widget.controller.text.length < 6                        ? lightRedColor                        : pantonColor                    : pantonColor),                fontWeight: FontWeight.w400,              ),            ),          ),          TextFormField(            onChanged: widget.onChanged,            autofocus: true,            focusNode: widget.inputNode,            controller: widget.controller,            obscureText: obscureText,            decoration: InputDecoration(              counterText: "",              focusedBorder: OutlineInputBorder(                borderSide: BorderSide(                  color: Color(widget.controller.text.isNotEmpty                      ? widget.controller.text.length < 6                          ? lightRedColor                          : pantonColor                      : pantonColor),                  width: 2,                ),                borderRadius: BorderRadius.circular(13.0),              ),              enabledBorder: OutlineInputBorder(                borderSide: BorderSide(                  color: Color(widget.controller.text.isNotEmpty                      ? widget.controller.text.length < 6                          ? lightRedColor                          : pantonColor                      : pantonColor),                  width: 2,                ),                borderRadius: BorderRadius.circular(13.0),              ),              isDense: true,              filled: true,              fillColor: const Color(0xFF141517),              suffixIcon: GestureDetector(                onTap: () {                  setState(() {                    obscureText = !obscureText;                  });                },                child: Image.asset(                  obscureText                      ? 'assets/icons/ic_show_16.png'                      : 'assets/icons/ic_hide_16.png',                ),              ),            ),            cursorColor: const Color(pantonColor),            style: GoogleFonts.poppins(fontSize: 14, color: Colors.white),          ),          Padding(            padding: const EdgeInsets.only(left: 15, top: 5),            child: Text(              widget.controller.text.isNotEmpty ? 'At least 6 characters' : '',              style: GoogleFonts.poppins(                fontSize: 14,                color: Color(                  widget.controller.text.length < 6                      ? lightRedColor                      : pantonColor,                ),                fontWeight: FontWeight.w400,              ),            ),          ),        ],      ),    );  }}