import 'package:flutter/material.dart';import 'package:flutter/services.dart';import 'package:google_fonts/google_fonts.dart';import '../../utils/utils.dart';Widget inputPhoneWidget(TextEditingController controller, FocusNode inputNode) {  return SingleChildScrollView(      child: Column(    crossAxisAlignment: CrossAxisAlignment.start,    children: [      Padding(        padding: const EdgeInsets.only(left: 15, bottom: 5),        child: Text(          'Telefon raqami',          style: GoogleFonts.poppins(              fontSize: 14,              color: const Color(pantonColor),              fontWeight: FontWeight.w400),        ),      ),      TextFormField(        inputFormatters: <TextInputFormatter>[          FilteringTextInputFormatter.allow(RegExp(r'[0-9]')),        ],        maxLength: 9,        keyboardType: TextInputType.number,        autofocus: true,        focusNode: inputNode,        controller: controller,        decoration: InputDecoration(          counterText: "",          focusedBorder: OutlineInputBorder(            borderSide: const BorderSide(color: Color(pantonColor), width: 2),            borderRadius: BorderRadius.circular(13.0),          ),          enabledBorder: OutlineInputBorder(            borderSide: const BorderSide(color: Color(pantonColor), width: 2),            borderRadius: BorderRadius.circular(13.0),          ),          isDense: true,          filled: true,          fillColor: const Color(0xFF141517),          prefixText: '+998 ',          labelText: '+998',          labelStyle: GoogleFonts.poppins(fontSize: 14, color: Colors.white),          floatingLabelBehavior: FloatingLabelBehavior.never,          prefixStyle: GoogleFonts.poppins(fontSize: 15, color: Colors.white),        ),        cursorColor: const Color(pantonColor),        style: GoogleFonts.poppins(fontSize: 14, color: Colors.white),      )    ],  ));}