import 'package:carousel_slider/carousel_slider.dart';import 'package:flutter/material.dart';class ImageSlider extends StatefulWidget {  final List<String> imgList;  const ImageSlider({required this.imgList, Key? key}) : super(key: key);  @override  State<ImageSlider> createState() => _ImageSliderState();}class _ImageSliderState extends State<ImageSlider> {  final CarouselController buttonCarouselController = CarouselController();  int _current = 0;  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: Colors.transparent,      body: Column(        children: [          SizedBox(              height: 100,              width: double.infinity,              child: CarouselSlider(                options: CarouselOptions(                  autoPlay: true,                  enlargeCenterPage: false,                  viewportFraction: 0.850,                  onPageChanged: (index, reason) {                    setState(() {                      _current = index;                    });                  },                ),                disableGesture: true,                carouselController: buttonCarouselController,                items: widget.imgList                    .map(                      (item) => Padding(                        padding: const EdgeInsets.symmetric(horizontal: 5),                        child: Center(                          child: ClipRRect(                            borderRadius: BorderRadius.circular(10),                            child: Image.asset(                              item.toString() ?? '',                              fit: BoxFit.cover,                            ),                          ),                        ),                      ),                    )                    .toList(),              )),        ],      ),    );  }}